mapper.update:
update t_order
set `name` = #{name,jdbcType=VARCHAR},
  price = #{price,jdbcType=DOUBLE},
  dname = #{dname,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, `name`, price, dname
from t_order
where id = #{id,jdbcType=INTEGER}

